mapper.select:
SELECT 

FROM sys_user_online_log a

WHERE a.id = #{id}

SELECT 

FROM sys_user_online_log a

WHERE a.del_flag = 0

AND a.login_name = #{loginName}


AND a.login_time = #{loginTime}

SELECT 

FROM sys_user_online_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT 

FROM sys_user_online_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_user_online_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
